import React from "react"
import {Link, useRouteMatch} from "react-router-dom"

function Breadcrumbs({crumbs}) {
  const {path} = useRouteMatch()
  let crumbsArray = []
  crumbs.map( (crumb) => {
    return <li className="breadcrumb-item"> <Link to={crumb.link}>{crumb.label}</Link></li>
  } )

  crumbsArray.push(<li className="breadcrumb-item"> <Link to="/">Home</Link></li>)

  for (let i = 0; i < crumbs.length -1 ; i++){
    crumbsArray.push(<li className="breadcrumb-item"> <Link to={crumbs[i].link}>{crumbs[i].label}</Link></li>)
  }

  crumbsArray.push(<li className="breadcrumb-item active" aria-current="page">{crumbs[crumbs.length - 1].label}</li>)

  return (
    <nav aria-label="breadcrumb">
      <ol className="breadcrumb">
        {crumbsArray}
      </ol>
    </nav>
  )

}

export default Breadcrumbs
